package ru.fit.app.diimport com.arkivanov.decompose.ComponentContextimport org.kodein.di.DIimport org.kodein.di.bindimport org.kodein.di.factoryimport ru.fit.app.presentation.RootComponentval RootModule = DI.Module("RootModule") {	bind<RootComponent>() with factory { componentContext: ComponentContext ->		RootComponent(			componentContext = componentContext,		)	}}