package ru.fit.app.presentation.navigationimport com.arkivanov.decompose.router.stack.StackNavigationimport com.arkivanov.decompose.router.stack.bringToFrontimport ru.fit.app.features.exercise.presentation.MainNavigationimport ru.fit.app.presentation.RootComponentclass NavigationImpl(	private val router: StackNavigation<RootComponent.Config>) : MainNavigation {	override fun navigateToWorkout(id: Int) {		router.bringToFront(RootComponent.Config.Workout(id))	}	override fun navigateToProfile() {		router.bringToFront(RootComponent.Config.Profile)	}}