package ru.fit.app.design.component.uiimport androidx.compose.foundation.backgroundimport androidx.compose.foundation.clickableimport androidx.compose.foundation.layout.Arrangementimport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.Rowimport androidx.compose.foundation.layout.fillMaxWidthimport androidx.compose.foundation.layout.paddingimport androidx.compose.foundation.layout.sizeimport androidx.compose.foundation.shape.RoundedCornerShapeimport androidx.compose.material.icons.Iconsimport androidx.compose.material.icons.filled.ChevronRightimport androidx.compose.material3.Iconimport androidx.compose.material3.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.text.style.TextOverflowimport androidx.compose.ui.unit.dpimport androidx.compose.ui.unit.spimport ru.fit.app.FitTheme@Composablefun ProgressButton(	title: String,	subtitle: String,	onClick: () -> Unit,	modifier: Modifier = Modifier) {	Row(		modifier = modifier			.fillMaxWidth()			.background(				color = FitTheme.colors.bGTertiary,				shape = RoundedCornerShape(12.dp)			)			.clickable { onClick() }			.padding(horizontal = 16.dp, vertical = 12.dp),		horizontalArrangement = Arrangement.SpaceBetween,		verticalAlignment = Alignment.CenterVertically	) {		Column(			modifier = Modifier.weight(1f),			verticalArrangement = Arrangement.spacedBy(8.dp)		) {			Text(				text = title,				fontSize = 16.sp,				color = FitTheme.colors.fondPrimary,				maxLines = 1,				overflow = TextOverflow.Ellipsis			)			Text(				text = subtitle,				fontSize = 14.sp,				color = FitTheme.colors.fondTertiary,				maxLines = 1,				overflow = TextOverflow.Ellipsis			)		}		Icon(			imageVector = Icons.Default.ChevronRight,			contentDescription = null,			tint = FitTheme.colors.fondPrimary,			modifier = Modifier.size(24.dp)		)	}}@Composablefun ProgressButtonPreview() {	ProgressButton(		title = "View Progress with Very Long Title That Should Be Truncated",		subtitle = "Weekly Stats and Additional Information",		onClick = {},		modifier = Modifier.padding(16.dp)	)}