package ru.fit.app.features.exercise.details.diimport com.arkivanov.decompose.ComponentContextimport org.kodein.di.DIimport org.kodein.di.bindimport org.kodein.di.factoryimport org.kodein.di.instanceimport ru.fit.app.features.exercise.details.presentation.ExerciseComponentdata class ExerciseArgs(	val componentContext: ComponentContext)val ExerciseModule = DI.Module("ExerciseModule") {	bind<ExerciseComponent>() with factory { args: ExerciseArgs ->		ExerciseComponent(			getExerciseListUseCase = instance(),			componentContext = args.componentContext,		)	}}