package ru.fit.app.features.exercise.uiimport androidx.compose.foundation.layout.Arrangementimport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.Spacerimport androidx.compose.foundation.layout.fillMaxSizeimport androidx.compose.foundation.layout.heightimport androidx.compose.foundation.layout.paddingimport androidx.compose.material3.MaterialThemeimport androidx.compose.material3.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.unit.dpimport ru.fit.app.FitThemeimport ru.fit.app.design.component.ui.AddButton@Composablefun EmptyContent(	onAddWorkout: () -> Unit,) {	Column(		modifier = Modifier.fillMaxSize().padding(horizontal = 16.dp),		horizontalAlignment = Alignment.CenterHorizontally,		verticalArrangement = Arrangement.Center	) {		Text(			text = "Нет тренировок в этот день",			style = MaterialTheme.typography.titleMedium,			color = FitTheme.colors.fondPrimary,		)		Spacer(Modifier.height(16.dp))		AddButton(			onClick = onAddWorkout,		)	}}