package ru.fit.app.features.profile.diimport com.arkivanov.decompose.ComponentContextimport org.kodein.di.DIimport org.kodein.di.bindimport org.kodein.di.factoryimport org.kodein.di.instanceimport ru.fit.app.features.profile.presentation.ProfileComponentdata class ProfileComponentArgs(	val componentContext: ComponentContext,	val onMainSelected: () -> Unit,	val onProgressSelected: () -> Unit)val ProfileModule = DI.Module("ProfileModule") {	bind<ProfileComponent>() with factory { args: ProfileComponentArgs ->		ProfileComponent(			componentContext = args.componentContext,			getUserProfileUseCase = instance(),			onMainSelected = args.onMainSelected,			onProgressSelected = args.onProgressSelected		)	}}