package ru.fit.app.features.profile.diimport com.arkivanov.decompose.ComponentContextimport org.kodein.di.DIimport org.kodein.di.bindimport org.kodein.di.factoryimport org.kodein.di.instanceimport ru.fit.app.features.profile.presentation.ProfileComponentimport ru.fit.app.features.profile.presentation.ProfileNavigationdata class ProfileComponentArgs(	val componentContext: ComponentContext,	val navigation: ProfileNavigation,)val ProfileModule = DI.Module("ProfileModule") {	bind<ProfileComponent>() with factory { args: ProfileComponentArgs ->		ProfileComponent(			componentContext = args.componentContext,			getUserProfileUseCase = instance(),			profileNavigation = args.navigation,		)	}}