package ru.fit.app.features.profile.uiimport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.fillMaxWidthimport androidx.compose.material.Buttonimport androidx.compose.material.Textimport androidx.compose.material.icons.Iconsimport androidx.compose.material.icons.automirrored.filled.ArrowBackimport androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport ru.fit.app.design.component.ui.AppBarimport ru.fit.app.features.profile.presentation.ProfileComponent@Composablefun ScreenProfile(component: ProfileComponent) {	Column(		modifier = Modifier.fillMaxWidth(), horizontalAlignment = Alignment.CenterHorizontally	) {		AppBar(			title = "Profile",			leftIcon = Icons.AutoMirrored.Filled.ArrowBack,			onLeftButtonClick = component::onBack		)		Text("ScreenProfile")		Button(			onClick = component::navigationProgress		) {			Text("Progress")		}	}}