package ru.fit.app.features.details.diimport com.arkivanov.decompose.ComponentContextimport org.kodein.di.DIimport org.kodein.di.bindimport org.kodein.di.factoryimport org.kodein.di.instanceimport ru.fit.app.features.details.presentation.DetailsComponentdata class DetailsArgs(	val componentContext: ComponentContext,	val id: Int)val DetailsModule = DI.Module("DetailsModule") {	bind<DetailsComponent>() with factory { args: DetailsArgs ->		DetailsComponent(			componentContext = args.componentContext,			getWorkoutUseCase = instance(),			id = args.id,		)	}}