package ru.fit.app.features.details.uiimport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.fillMaxWidthimport androidx.compose.material.CircularProgressIndicatorimport androidx.compose.material.Textimport androidx.compose.runtime.Composableimport androidx.compose.runtime.LaunchedEffectimport androidx.compose.runtime.collectAsStateimport androidx.compose.runtime.getValueimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport ru.fit.app.features.details.presentation.DetailsComponentimport ru.fit.app.features.details.presentation.DetailsState@Composablefun ScreenDetails(	component: DetailsComponent,) {	val screenState by component.screenState.collectAsState()	Column(		modifier = Modifier.fillMaxWidth(), horizontalAlignment = Alignment.CenterHorizontally	) {		when (val state = screenState) {			DetailsState.Initial    -> {				LaunchedEffect(Unit) {					component.loadContent()				}			}			DetailsState.Error      -> Text("Error")			DetailsState.Loading    -> CircularProgressIndicator()			is DetailsState.Content -> {				Content()			}		}	}}