package ru.fit.app.features.selection.diimport com.arkivanov.decompose.ComponentContextimport org.kodein.di.DIimport org.kodein.di.bindimport org.kodein.di.factoryimport ru.fit.app.features.selection.presentation.SelectionComponentdata class SelectionArgs(	val componentContext: ComponentContext)val SelectionModule = DI.Module("SelectionModule") {	bind<SelectionComponent>() with factory { args: SelectionArgs ->		SelectionComponent(componentContext = args.componentContext)	}}