package ru.fit.app.features.selection.uiimport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.fillMaxWidthimport androidx.compose.material.CircularProgressIndicatorimport androidx.compose.material.Textimport androidx.compose.runtime.Composableimport androidx.compose.runtime.LaunchedEffectimport androidx.compose.runtime.collectAsStateimport androidx.compose.runtime.getValueimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport ru.fit.app.features.selection.presentation.SelectionComponentimport ru.fit.app.features.selection.presentation.SelectionState@Composablefun ScreenSelection(	component: SelectionComponent,) {	val screenState by component.screenState.collectAsState()	Column(		modifier = Modifier.fillMaxWidth(), horizontalAlignment = Alignment.CenterHorizontally	) {		when (val state = screenState) {			SelectionState.Initial    -> {				LaunchedEffect(Unit) {					component.loadContent()				}			}			SelectionState.Error      -> Text("Error")			SelectionState.Loading    -> CircularProgressIndicator()			is SelectionState.Content -> {				Content()			}		}	}}