package ru.fit.app.features.shared.uiimport androidx.compose.foundation.layout.Columnimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport com.arkivanov.decompose.extensions.compose.stack.Childrenimport com.arkivanov.decompose.extensions.compose.stack.animation.fadeimport com.arkivanov.decompose.extensions.compose.stack.animation.stackAnimationimport ru.fit.app.features.details.ui.ScreenDetailsimport ru.fit.app.features.selection.ui.ScreenSelectionimport ru.fit.app.features.shared.presentation.FeatureWorkoutComponentimport ru.fit.app.features.workout.ui.ScreenWorkout@Composablefun ScreenFeatureWorkout(	component: FeatureWorkoutComponent,	modifier: Modifier = Modifier,) {	Column(modifier = modifier) {		Children(			stack = component.stack,			modifier = modifier,			animation = stackAnimation(fade()),		) {			when (val child = it.instance) {				is FeatureWorkoutComponent.Child.Workout   -> ScreenWorkout(child.component)				is FeatureWorkoutComponent.Child.Selection -> ScreenSelection(child.component)				is FeatureWorkoutComponent.Child.Details   -> ScreenDetails(child.component)			}		}	}}