package ru.fit.app.features.workout.diimport com.arkivanov.decompose.ComponentContextimport org.koin.dsl.moduleimport ru.fit.app.features.workout.data.repository.WorkoutRepositoryImplimport ru.fit.app.features.workout.domain.repository.WorkoutRepositoryimport ru.fit.app.features.workout.domain.usecase.GetWorkoutUseCaseimport ru.fit.app.features.workout.presentation.WorkoutComponentval WorkoutModule = module {	factory<WorkoutRepository> { WorkoutRepositoryImpl() }	factory { GetWorkoutUseCase(get()) }	factory { (componentContext: ComponentContext, id: Int) ->		WorkoutComponent(			componentContext = componentContext,			getWorkoutUseCase = get(),			id = id		)	}}