package ru.fit.app.features.workout.uiimport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.fillMaxWidthimport androidx.compose.material.CircularProgressIndicatorimport androidx.compose.material.Textimport androidx.compose.runtime.Composableimport androidx.compose.runtime.LaunchedEffectimport androidx.compose.runtime.collectAsStateimport androidx.compose.runtime.getValueimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport ru.fit.app.features.workout.presentation.WorkoutComponentimport ru.fit.app.features.workout.presentation.WorkoutState@Composablefun ScreenWorkout(	component: WorkoutComponent,) {	val screenState by component.screenState.collectAsState()	Column(		modifier = Modifier.fillMaxWidth(), horizontalAlignment = Alignment.CenterHorizontally	) {		when (val state = screenState) {			WorkoutState.Initial    -> {				LaunchedEffect(Unit) {					component.loadContent()				}			}			WorkoutState.Error      -> Text("Error")			WorkoutState.Loading    -> CircularProgressIndicator()			is WorkoutState.Content -> {				Content(					training = state.training,					onSelectionClick = component::navigationSelection,					onDetailsClick = component::navigationDetails,				)			}		}	}}