package ru.fit.app.features.workout.uiimport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.fillMaxWidthimport androidx.compose.material.CircularProgressIndicatorimport androidx.compose.material.MaterialThemeimport androidx.compose.material.Textimport androidx.compose.runtime.Composableimport androidx.compose.runtime.LaunchedEffectimport androidx.compose.runtime.collectAsStateimport androidx.compose.runtime.getValueimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport ru.fit.app.features.workout.presentation.Stateimport ru.fit.app.features.workout.presentation.WorkoutComponent@Composablefun ScreenWorkout(	id: Int,	component: WorkoutComponent,) {	val screenState by component.screenState.collectAsState()	MaterialTheme {		Column(			modifier = Modifier.fillMaxWidth(), horizontalAlignment = Alignment.CenterHorizontally		) {			when (val state = screenState) {				State.Initial    -> {					LaunchedEffect(Unit) {						component.loadContent(id)					}				}				State.Error      -> Text("Error")				State.Loading    -> CircularProgressIndicator()				is State.Content -> {					Content(training = state.training)				}			}		}	}}