package ru.fit.app.shared.training.data.converterimport kotlinx.datetime.Instantimport org.junit.jupiter.api.Assertions.assertEqualsimport org.junit.jupiter.api.Testimport ru.fit.app.shared.training.data.module.TrainingModuleimport ru.fit.app.shared.training.domain.entity.Trainingclass ConverterTrainingTest {	private val converter = ConverterTraining()	@Test	fun `test invoke should convert TrainingModule to Training`() {		val actual = listOf(			Training(1, "Cardio", Instant.DISTANT_PAST, listOf()),			Training(2, "Strength", Instant.DISTANT_PAST, listOf())		)		val trainingModules = listOf(			TrainingModule(1, "Cardio", Instant.DISTANT_PAST, listOf()),			TrainingModule(2, "Strength", Instant.DISTANT_PAST, listOf())		)		val expected = converter(trainingModules)		assertEquals(expected, actual)	}}