package ru.fit.app.shared.training.data.repositoryimport io.mockk.coEveryimport io.mockk.mockkimport kotlinx.coroutines.test.runTestimport kotlinx.datetime.Instantimport ru.fit.app.shared.training.data.converter.ConverterTrainingimport ru.fit.app.shared.training.data.module.TrainingModuleimport ru.fit.app.shared.training.data.network.TrainingApiimport ru.fit.app.shared.training.domain.entity.Trainingimport ru.fit.app.shared.training.domain.repository.TrainingRepositoryimport kotlin.test.Testimport kotlin.test.assertEqualsclass TrainingRepositoryImplTest {	private val trainingApi: TrainingApi = mockk<TrainingApi> {}	private val converterTraining: ConverterTraining = mockk<ConverterTraining> {}	private val repository: TrainingRepository = TrainingRepositoryImpl(trainingApi, converterTraining)	@Test	fun `test getAll returns converted trainings`() = runTest {		val trainingModules = listOf(			TrainingModule(1, "Training 1", Instant.DISTANT_PAST, emptyList()),			TrainingModule(2, "Training 2", Instant.DISTANT_PAST, emptyList()),		)		val convertedTrainings = listOf(			Training(1, "Training 1", Instant.DISTANT_PAST, emptyList()),			Training(2, "Training 2", Instant.DISTANT_PAST, emptyList())		)		coEvery { trainingApi.getAll() } returns trainingModules		coEvery { converterTraining(trainingModules) } returns convertedTrainings		val result = repository.getAll()		assertEquals(convertedTrainings, result)	}}